import { useQuery } from "@tanstack/react-query";
import { fetchCoinHistory } from "../../api";
import { useCoinId } from "./Coin";
import ReactApexChart from "react-apexcharts";

interface IHistoricalData {
  time_open: number;
  time_close: number;
  open: string;
  high: string;
  low: string;
  close: string;
  volume: string;
  market_cap: number;
}

function Chart() {
  const { coinId } = useCoinId();
  const { isLoading, data } = useQuery<IHistoricalData[]>(
    ["ohlcv", coinId],
    () => fetchCoinHistory(coinId)
  );
  return (
    <div>
      {isLoading ? (
        "Loading"
      ) : (
        <ReactApexChart
          type="line"
          series={[
            {
              name: "hello",
              data: [1, 2, 3, 4, 20],
            },
            {
              name: "sales",
              data: [15, 18, 15, 100, 20],
            },
          ]}
          options={{
            theme: {
              mode: "dark",
            },
            chart: {
              height: 15500,
              width: 1500,
            },
          }}
        />
      )}
    </div>
  );
}

export default Chart;
