import { useQuery } from "@tanstack/react-query";
import { fetchCoinHistory } from "../../api";
import { useCoinId } from "./Coin";
import ReactApexChart from "react-apexcharts";

interface IHistoricalData {
  time_open: number;
  time_close: number;
  open: string;
  high: string;
  low: string;
  close: string;
  volume: string;
  market_cap: number;
}

function Chart() {
  const { coinId } = useCoinId();
  const { isLoading, data } = useQuery<IHistoricalData[]>(
    ["ohlcv", coinId],
    () => fetchCoinHistory(coinId)
  );
  return (
    <div>
      {isLoading ? (
        "Loading"
      ) : (
        <ReactApexChart
          type="line"
          height={300}
          width={500}
          series={[
            {
              name: "sales",
              data: data?.map((price) => parseFloat(price.close)) as number[],
            },
          ]}
          options={{
            colors: ["#00b894"],
            theme: {
              mode: "dark",
            },
            stroke: {
              curve: "smooth",
              width: 3,
            },
            chart: {
              toolbar: {
                show: false,
              },
              background: "#2d3436",
            },
            grid: {
              show: true,
              borderColor: "#90A4AE",
              strokeDashArray: 0,
              position: "back",
              xaxis: {
                lines: {
                  show: false,
                },
              },
              yaxis: {
                lines: {
                  show: false,
                },
              },
              row: {
                colors: undefined,
                opacity: 0.5,
              },
              column: {
                colors: undefined,
                opacity: 0.5,
              },
              padding: {
                top: 0,
                right: 0,
                bottom: 0,
                left: 0,
              },
            },
          }}
        />
      )}
    </div>
  );
}

export default Chart;
